import React from 'react';

function Game() {

  const numFields = 4;

  const handleChange = e => {
    const { maxLength, value, name } = e.target;
    const [fieldName, fieldIndex] = name.split("-");

    // if value length is greater than max character length
    if (value.length >= maxLength) {
      // if not last input field
      if (parseInt(fieldIndex, 10) < 3) {
        // Get the next input field
        const nextField = document.querySelector(
          `input[name=ssn-${parseInt(fieldIndex, 10) + 1}]`
        );

        // if , focus the next field
        if (nextField !== null) {
          nextField.focus();
        }
      }
    }
  }

  return (
    <div>
      <div>
        Guess the number combination!
      </div>
      
      <form>
        <input type='text' id='first' name='first' maxLength={1} size='1' onChange={handleChange} />
        <input type='text' id='second' name='second' maxLength={1} size='1' onChange={handleChange} />
        <input type='text' id='third' name='third' maxLength={1} size='1' onChange={handleChange} />
        <input type='text' id='fourth' name='fourth' maxLength={1} size='1' onChange={handleChange} />
        <button type='submit'>guess!</button>
      </form>
    </div>
  );
}

export default Game;
